
public class Star {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int i , j;
	    for ( i = 1; i < 5 ; i++ ) 
	    {
	        for (  j = i ; j < 5 ;j++   ) { 
	            System.out.print(" * ");
	        }
	        System.out.println("\n");

	    }
	}

}
